// Get incoming JSON from previous node
const data = items[0].json;

// Initialize defaults
let sessionId = null;
let chatInput = null;

// Check if it's a "messages" type payload with text
if (Array.isArray(data.messages) && data.messages.length > 0) {
    const msg = data.messages[0];
    sessionId = msg.from || null;
    chatInput = msg.text?.body || null;
}

// If no messages, maybe it's a status update - extract recipient_id as sessionId
if (!sessionId && Array.isArray(data.statuses) && data.statuses.length > 0) {
    sessionId = data.statuses[0].recipient_id || null;
    chatInput = null; // No text in statuses
}

// Output result
return [
  {
    json: {
      sessionId,
      chatInput
    }
  }
];

// Loop over input items and change the MIME type of binary data
for (const item of $input.all()) {
  // Check if the item has binary data
  if (item.binary) {
    // Find the binary property name (assuming there's at least one)
    const binaryPropertyNames = Object.keys(item.binary);

    for (const propName of binaryPropertyNames) {
      // If the MIME type is 'audio/mp3', change it to 'audio/mpeg'
      if (item.binary[propName].mimeType === "audio/mp3") {
        item.binary[propName].mimeType = "audio/mpeg";
      }
    }
  }
}

return $input.all();
